fn find_outlier(values: &[i32]) -> i32 {
    let target_remainder = match values[..3].iter().filter(|x| *x % 2 == 0).count() {
        2.. => 1,
        _ => 0
    };

    *values.iter().find(|x| x.abs() % 2 == target_remainder).unwrap()
}

